ALTER TABLE t_p1573360_game_store_platform.reviews 
ADD COLUMN IF NOT EXISTS likes INTEGER DEFAULT 0,
ADD COLUMN IF NOT EXISTS platform VARCHAR(50);

INSERT INTO t_p1573360_game_store_platform.reviews (game_id, user_name, rating, comment, is_verified, platform, likes, created_at) VALUES
(1, 'Александр М.', 5, 'Невероятная космическая RPG! Графика просто фантастическая, сюжет захватывает с первых минут. Побочные квесты интересные, исследование планет затягивает. Bethesda на высоте!', true, 'Xbox', 142, '2024-09-15 14:30:00'),
(1, 'Дмитрий К.', 4, 'Отличная игра, но есть технические проблемы. Иногда проседает FPS на старых консолях. В остальном - классика Bethesda с огромным миром и свободой действий.', true, 'PC', 87, '2024-09-20 10:15:00'),
(1, 'Елена Р.', 5, 'Лучшая игра года! Потратила уже 120+ часов и не могу оторваться. Космические станции, корабли, планеты - всё проработано до мелочей.', true, 'Xbox', 203, '2024-09-25 18:45:00'),

(2, 'Игорь В.', 5, 'Человек-паук 2 превзошёл все ожидания! Два героя, отличный сюжет про Венома, новые способности. Лучший эксклюзив PS5!', true, 'PlayStation', 315, '2024-09-18 12:00:00'),
(2, 'Мария С.', 5, 'Графика просто космос! Свинг по Нью-Йорку никогда не был таким плавным. Режим фоторежима - отдельное искусство. Обязательно к прохождению!', true, 'PlayStation', 278, '2024-09-22 16:30:00'),
(2, 'Олег Н.', 4, 'Очень круто, но немного короткая. Прошёл за 15 часов основной сюжет. Хотелось бы больше контента. Но качество на высоте!', true, 'PlayStation', 156, '2024-09-28 09:20:00'),

(3, 'Андрей П.', 5, 'Лучший гоночный симулятор! Мексика выглядит потрясающе, машины ведут себя реалистично. Онлайн режим огонь!', true, 'Xbox', 189, '2024-09-10 11:00:00'),
(3, 'Виктория Л.', 5, 'Играю с семьёй - всем нравится! Красивая графика, много машин, интересные гонки. Можно часами кататься по открытому миру.', true, 'PC', 234, '2024-09-14 15:45:00'),

(4, 'Сергей Б.', 5, 'God of War Ragnarök - шедевр! Кратос и Атрей в новом приключении. Боевая система улучшена, босс-файты эпичные. 10/10', true, 'PlayStation', 421, '2024-09-12 13:30:00'),
(4, 'Наталья Ф.', 5, 'Плакала на концовке. История невероятная, персонажи живые. Графика лучшая на PS5. Игра года без вопросов!', true, 'PlayStation', 512, '2024-09-16 17:00:00'),
(4, 'Максим Д.', 4, 'Отличное продолжение, но первая часть мне понравилась больше. Всё равно must-have для владельцев PlayStation.', true, 'PlayStation', 167, '2024-09-24 10:30:00'),

(5, 'Павел Ш.', 4, 'Halo вернулся! Мультиплеер бесплатный и затягивает. Кампания короткая, но интересная. Ждём новый контент.', true, 'Xbox', 198, '2024-09-11 14:00:00'),
(5, 'Артём Ч.', 3, 'Хорошая игра, но не хватает контента на старте. Надеюсь, будут обновления. Геймплей классический Halo.', true, 'Xbox', 89, '2024-09-19 11:30:00'),

(6, 'Юлия К.', 5, 'The Last of Us Part II - произведение искусства. Сюжет спорный, но подача на высочайшем уровне. Графика и анимации - лучшие в индустрии.', true, 'PlayStation', 634, '2024-09-08 16:00:00'),
(6, 'Владимир Т.', 5, 'Самая атмосферная игра! Каждая локация проработана, враги умные. Прошёл на высокой сложности - незабываемые эмоции.', true, 'PlayStation', 445, '2024-09-13 12:45:00'),
(6, 'Анна М.', 4, 'Противоречивая, но качественная игра. Геймплей отличный, графика шикарная. Сюжет разделил фанатов, но мне понравилось.', true, 'PlayStation', 287, '2024-09-21 15:15:00'),

(18, 'Кирилл А.', 5, 'Call of Duty снова на высоте! Мультиплеер затягивает, новые карты отличные. Оптимизация лучше чем в прошлых частях.', true, 'PlayStation', 267, '2024-09-17 13:00:00'),
(18, 'Светлана П.', 4, 'Хорошая часть серии, но ничего революционного. Кампания короткая, зато мультиплеер топ. Зомби режим классный!', true, 'Xbox', 156, '2024-09-23 10:00:00'),

(19, 'Роман Г.', 5, 'Mortal Kombat 1 - лучший файтинг! Фаталити жестокие, новые персонажи интересные. Сюжет с перезагрузкой вселенной крутой!', true, 'PlayStation', 298, '2024-09-09 14:30:00'),
(19, 'Евгения Н.', 5, 'Графика невероятная, каждый удар чувствуется. Играю с друзьями онлайн - море эмоций! NetherRealm молодцы!', true, 'Xbox', 234, '2024-09-15 16:00:00'),

(22, 'Денис М.', 5, 'Diablo IV - игра мечты для фанатов серии! Темная атмосфера, сложные данжи, крутой лут. Сезоны держат интерес!', true, 'Xbox', 389, '2024-09-11 11:00:00'),
(22, 'Алиса В.', 4, 'Хорошая ARPG, но хотелось бы больше разнообразия. Графика отличная, музыка мрачная. Играю с друзьями - весело!', true, 'PC', 178, '2024-09-18 13:30:00'),

(23, 'Григорий К.', 5, 'Star Wars Jedi: Survivor - космос! Световые мечи, силы джедая, огромные миры. Respawn сделали шедевр!', true, 'PlayStation', 412, '2024-09-10 15:00:00'),
(23, 'Ольга С.', 5, 'Лучшая игра по Звёздным войнам! Сюжет захватывающий, боссы сложные. Фанатам саги - обязательно!', true, 'Xbox', 356, '2024-09-16 12:00:00');
